package invocation;

public class Ex4 {

	public static int minhaFuncao(int numero) {
		int resultado = numero + 10;
		return resultado;
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(minhaFuncao(3));
		System.out.println(minhaFuncao(5));
		System.out.println(minhaFuncao(9));
		// Sempre de dentro para fora do parenteses
	}

}
